sourceCode:
/* ==UserStyle==
@name           Jam-M
@namespace      https://github.com/Catgrills/Jam-M
@version        1.0.0
@description    An userstyle for Soundcloud called Jam-M.
@author         Catgrills/Katgrills
@preprocessor 	uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #F0F1F5 !important;
		--card-bg: #FFF !important;
		--border-color: #EBEBEB !important;
		--primary-color: #333 !important;
		--secondary-color: #7E7E7E !important;
		--filter: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #33393E !important; 
		--card-bg: #3C4249 !important;
		--border-color: #3C3741 !important;
		--primary-color: #FFF !important;
		--secondary-color: #E1EFE6 !important;
		--filter: brightness(0) invert(1) !important;
	}
	`
}
==/UserStyle== */

@-moz-document domain("soundcloud.com") {
/*[[theme]]*/
/* GENERAL */

	/** DESCRIPTION : change the elements of all page **/
	* {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
	}

    *::-moz-focus-inner {
        border: 0;
    }

	body,
	#app {
		background: var(--body-bg) !important;
	}

	body {
		overflow-x: hidden;
	}

	.l-fluid-fixed {
		width: 90% !important;
		position: absolute !important;
		left: 0px !important;
		right: 0px !important;
		margin: auto !important;
	}

    .l-fluid-fixed .sc-border-light-right {
        border-right: none !important;
    }

	.l-container.l-content {
		padding-top: 110px !important;
	}

	.creatorsNavigation__onSoundCloud,
	.dashbox__wrapper,
	.image.m-station::before,
	.l-footer,
	.listenArtistInfo__copyright,
	.l-main .userNetwork .sc-button-share,
	.paging-eof,
	.premiumIconLink,
	.profileMenu__link.profileMenu__premium,
	.sc-tag::before,
    .searchPremiumContentFooter,
	.tagsMain .tabs__tabs,
    .textfield__clear,
	.truncatedAudioInfo__license,
    .truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after {
		display: none !important;
	}

    .emptyNetworkPage__headline,
    .inSetsList__empty *,
    .searchList__emptyText {
        color: var(--primary-color) !important;
        font-size: 16pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .sc-type-h3.emptyNetworkPage__action {
        font-size: 16pt !important;
        font-stretch: condensed !important;        
    }

    .gritter-item-wrapper {
     	background: var(--card-bg) !important;
    	border: 1px solid var(--border-color) !important;    
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
    }

    .gritter-item {
     	background: var(--card-bg) !important;
        border: none !important;
        box-shadow: none !important;
        padding: 15px !important;
    }

    .gritter-image {
     	display: none !important;
    }

    .gritter-item * {
     	color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

	/* HEADER */

	/** DESCRIPTION : change the elements of the header **/
	.header {

		/*** HEADER CONTAINER ***/
		background: #FCA65F !important;
	}

	.header__logo {
		background: #EB6361 !important;
	}

	.header li a {

		/*** HEADER LINKS ***/
		color: #FFF !important;
	}

	.header__navMenu > li > a {
		border-right: none !important;
		line-height: 21px !important;
	}

	.header__navMenu > li > a.selected,
	.uploadButton.selected {
		background: transparent;
	}

	.header__mainMenu-loggedInHome,
	.header__mainMenu-collection,
	.uploadButton__title,
	.userNav__username.sc-truncate {
		color: #FFF;
		border-right: none !important;
		font-size: 12pt !important;
		font-stretch: condensed !important;
		font-weight: bold;
        line-height: 21px !important;
		text-transform: uppercase;
	}

	.left.header__navWrapper * {
		font-size: 12pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		text-transform: uppercase !important;
	}

	.header__collection-wrapper {
		background: #0C545C !important;
		padding-left: 10px !important;
		padding-right: 10px !important;
		margin-right: -1px !important;
	}

	.header__link.header__goUpsell {
		display: none !important;
	}

	.headerSearch {

		/*** HEADER SEARCHBAR ***/
		background: #F0F1F5 !important;
		padding-bottom: 9px !important;
		margin-right: 10px !important;
		margin-left: 1px !important;
	}

	.headerSearch__input.sc-input {
		background: inherit !important;
		border-radius: 1px !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
	}

	.newItemBadge.newItems__some {
		border: 1px solid transparent !important;
	}

	.autocompleteItem {
		background: #212121 !important;
		padding: 5px !important;
		margin-bottom: -10px !important;
	}

	.autocompleteItem * {
		font-size: 14pt !important;
		font-stretch: condensed !important;		
        line-height: 16pt !important;
	}

	.autocompleteItem__title {
		font-weight: normal !important;
	}

	.autocompleteItem__queryIcon {
		display: none !important;
	}

	.searchMenu__searchForText {
		font-size: 14pt !important;
		font-stretch: condensed !important;
        font-weight: normal !important;
	}

	.autocompleteItem:last-child {
		border-bottom-left-radius: 2px !important;
		border-bottom-right-radius: 2px !important;
	}

	.userNav__item.selected,
	.userNav__button.selected,
	.header__navMenu > li > a.header__moreButton.selected {

		/*** HEADER SECTION ***/
		background-color: #3C3741;
	}

	.unreadConversations.g-dark,
	.profileMenu__list,
	.g-dark-bg {
		background-color: #3C3741 !important;
		border-bottom-right-radius: 2px !important;
		border-bottom-left-radius: 2px !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		padding: 20px !important;
		margin-left: 0px !important;
		margin-right: 1px !important;
	}

    .conversationBadge__imageDeleted {
        width: 40px !important;
        height: 40px !important;
    }

    .conversationBadge__missingUsername,
    .conversationBadge__time,
    .conversationBadge__body.sc-truncate {
        color: #FFF !important;
		font-size: 14pt !important;  
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

	.profileMenu__list.profileMenu__listener.sc-list-nostyle {
		margin-bottom: -40px !important;
	}

	.header__userNav {
		margin-left: 12px !important;
	}

	.g-dark-list {
		background-color: transparent !important;
	}

	.profileMenu__link {
		padding: 8px !important;
	}

	.profileMenu__link:hover,
	.profileMenu__link:focus {
		background: transparent !important;
	}

	.profileMenu__link::after {
		display: none;
	}

	.notifications__viewAll.sc-type-h3.latestActivities__viewAll,
	.notifications__viewAll.sc-type-h3.outgoing-messages.headerMessages__viewAll,
	.profileMenu__list:not(:last-child),.moreMenu__list,
    .profileMenu__link.profileMenu__trackManager {
		border: none;
		font-size: 14pt !important;
	}

	.g-dark-bg .userBadge.m-horizontal .userBadge__meta {
		display: none !important;
	}

	.g-dark-bg .sc-clearfix.sc-type-light.sc-truncate {
		margin-top: -10px !important;
		margin-left: 4px !important;
	}

	.g-dark-bg .userBadge .userBadge__title *,
    .g-dark-bg .userBadge .userBadge__title .sc-truncate {
		font-size: 14pt !important;
        font-weight: normal !important;
	}

	.g-dark-bg .ownActivity__content * {
		font-size: 14pt !important;
        font-weight: normal !important;
	}

	.g-dark .ownActivity .ownActivity__time {
		font-size: 14pt !important;
        font-weight: normal !important;
		top: 5px !important;
		right: 2px !important;
	}

	.g-dark .sc-icon-medium.left.commentTitle__icon.sc-icon-comment-light {
		display: none !important;
	}

	/* PLAYER */

	/** DESCRIPTION : change the elements of the player footer **/
	.playControls .playControls__inner {

		/*** PLAYER CONTAINER ***/
		background-color: var(--card-bg) !important;
		border-top: 1px solid var(--border-color) !important;
		padding: 10px 0px !important;
	}

	.playControls__bg {
		background-color: transparent;
		border-top: none;
	}

	.skipControl__previous {

		/*** PLAYER PREVIOUS BUTTON ***/
		background: no-repeat url("https://i.imgur.com/uNuXhE0.png") center !important;
		background-size: 17px auto !important;
        filter: var(--filter) !important;
	}

	.playControl[title="Play current"] {

		/*** PLAYER PLAY BUTTON ***/
		background: no-repeat url("https://i.imgur.com/QUuScCg.png") center !important;
		background-size: 24px auto !important;
	}

	.playControl[title="Pause current"] {

		/*** PLAYER PAUSE BUTTON ***/
		background: no-repeat url("https://i.imgur.com/es5blFI.png") center !important;
		background-size: 24px auto !important;
	}

	.skipControl__next {

		/*** PLAYER NEXT BUTTON ***/
		background: no-repeat url("https://i.imgur.com/1rnYfSY.png") center !important;
		background-size: 17px auto !important;
        filter: var(--filter) !important;
	}

	.shuffleControl::before {

		/*** PLAYER SHUFFLE BUTTON ***/
		background: no-repeat url("https://i.imgur.com/LycNdmB.png") center !important;
		background-size: 23px auto !important;
        filter: var(--filter) !important;
	}

	.shuffleControl::after {
		background: no-repeat url("https://i.imgur.com/vExkrH2.png") center !important;
		background-size: 23px auto !important;
	}

	.repeatControl.sc-ir.m-none,
	.repeatControl::before {

		/*** PLAYER REPEAT BUTTON ***/
		background: no-repeat url("https://i.imgur.com/4IYot9z.png") center !important;
		background-size: 18px auto !important;
        filter: var(--filter) !important;
	}

	.repeatControl::after {
		background: no-repeat url("https://i.imgur.com/mqAMFLM.png") center !important;
		background-size: 18px auto !important;
	}

	.playControls .playControls__inner .playbackTimeline__timePassed,
	.playControls .playControls__inner .playbackTimeline__duration {

		/*** PLAYER TIME ***/
        color: var(--primary-color);
		font-size: 14pt !important;
		font-stretch: condensed !important;
	}

    .playControls .playControls__inner .playbackTimeline__timePassed {
     	color: #FCA65F !important; 
    }

	.volume__sliderWrapper {
		background-color: var(--card-bg) !important;
	}

	.volume[data-level="1"] .volume__button,
	.volume[data-level="2"] .volume__button,
	.volume[data-level="3"] .volume__button,
	.volume[data-level="4"] .volume__button,
	.volume[data-level="5"] .volume__button {
		background: no-repeat url("https://i.imgur.com/qCaedk3.png") center !important;
		background-size: 17px auto !important;
	}

	.volume[data-level="6"] .volume__button,
	.volume[data-level="7"] .volume__button,
	.volume[data-level="8"] .volume__button,
	.volume[data-level="9"] .volume__button,
	.volume[data-level="10"] .volume__button {
		background: no-repeat url("https://i.imgur.com/tOGftUn.png") center !important;
		background-size: 17px auto !important;
	}

	.volume.muted .volume__button {
		background: no-repeat url("https://i.imgur.com/kjx816u.png") center !important;
		background-size: 17px auto !important;
	}

	.playControls .playControls__inner .sc-artwork {

		/*** PLAYER ALBUM ***/
		border-radius: 50% !important;
	}

	.playbackSoundBadge__titleContextContainer a {
		font-size: 14pt !important;
		font-stretch: condensed !important;
		max-width: 100% !important;
		overflow: hidden !important;
		text-overflow: ellipsis !important;
		text-transform: lowercase !important;
		white-space: nowrap !important;
	}

    .playbackSoundBadge__lightLink {
        color: var(--secondary-color) !important;        
    }

    .playbackSoundBadge__titleLink {
        color: var(--primary-color) !important;          
    }

	.playbackSoundBadge__titleLink,
	.playbackSoundBadge__lightLink {
		padding-bottom: 10px !important;
		margin-top: 0px !important;
	}

	.playbackSoundBadge__actions {

		/*** PLAYER ACTION BUTTONS ***/
		margin-left: 30px;
	}

	.playControls .sc-button-small.sc-button-selected.sc-button-like::before,
	.playControls .sc-button-small.sc-button-like::before,
    .playbackSoundBadge__showQueue {
		background-size: 21px 21px;
	}

    .sc-button-like.playbackSoundBadge__like[title="Like"],
    .playbackSoundBadge__showQueue {
        filter: var(--filter) !important;
	}


	.playbackSoundBadge__showQueue {
		margin-left: 4px !important;
	}

	/* STREAM */

	/** DESCRIPTION : change the elements of the stream page **/
	.stream__header.g-flex-row-centered-spread *,
	.userNetwork__subheadline,
	.relatedList__explanation,
	.tagsMain__title {

		/*** STREAM HEADER ***/
		font-size: 17pt !important;
		font-stretch: condensed !important;
		font-style: normal !important;
		font-weight: normal !important;
		letter-spacing: .1px !important;
	}

	.l-tabs {
		background: var(--card-bg) !important;
		border-bottom: 1px solid var(--border-color) !important;
		padding: 15px 20px !important;
		width: 100% !important;
		text-align: center !important;
		position: fixed !important;
		top: 46px !important;
		right: 0px !important;
		left: 0px !important;
		z-index: 999 !important;
	}

	.l-tabs .streamExploreTabs {
		margin-bottom: 3px;
	}

	.l-tabs .streamExploreTabs .g-tabs {
		border: none !important;
	}

	.l-tabs .streamExploreTabs .g-tabs .g-tabs-link {
        color: var(--primary-color);
		font-family: "Roboto" !important;
		font-size: 17pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
	}

	.l-tabs .streamExploreTabs .g-tabs-link.active,
	.l-tabs .streamExploreTabs .g-tabs-link:focus,
	.l-tabs .streamExploreTabs .g-tabs-link:hover {
		border-color: transparent !important;
        color: #FCA65F !important;
	}

	.soundList__item,
	.historicalPlays.m-player.lazyLoadingList .historicalPlays__item,
	.searchList__item {

		/*** STREAM SOUND LIST ITEM CONTAINER ***/
		background: var(--card-bg) !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 100px 30px 30px 30px !important;
		margin-bottom: 20px !important;
	}

	.soundList__item .soundContext {

		/*** STREAM SOUND LIST ITEM UPLOADER ***/
		border-bottom: 1px solid var(--border-color) !important;
		padding: 20px !important;
		position: absolute !important;
		right: 0px !important;
		left: 0px !important;
		margin-top: -100px !important;
	}

	.soundList__item .soundContext__line {
		background-image: none !important;
		font-size: 12pt !important;
		line-height: 28px !important;
	}

	.soundList__item .soundContext .g-avatar-badge-avatar span {
		border-radius: 2px !important;
	}

	.soundList__item .soundContext .soundContext__line {
		padding-left: 7px !important;
	}

	.soundList__item .soundContext .soundContext__line.sc-font-body,
    .soundList__item .soundContext .soundContext__line.sc-font-body * {
        color: var(--primary-color);
		font-size: 14pt !important;
        font-stretch: condensed;
		font-weight: normal !important;
	}

	.soundTitle__usernameText,
	.actorUser__username {
		font-size: 12pt !important;
		font-weight: normal !important;
	}

	.soundList__item .sound__content,
	.historicalPlays__item .sound__content,
	.searchList__item .sound__content {

		/*** STREAM SOUND LIST ITEM CONTENT ***/
		padding-left: 15px !important;
		overflow-x: visible !important;
	}

	.soundList__item .sc-button-play,
	.historicalPlays__item .sc-button-play,
	.searchList__item .sc-button-play {

		/*** STREAM SOUND LIST ITEM PLAY BUTTON ***/
		background: #FF9F55 !important;
		border-radius: 50% !important;
	}

	.soundList__item .soundTitle__usernameTitleContainer,
	.historicalPlays__item .soundTitle__usernameTitleContainer,
	.sidebarContent .soundTitle__usernameTitleContainer,
	.searchList__item .soundTitle__usernameTitleContainer {

		/*** STREAM SOUND LIST ITEM TITLE ***/
		padding-left: 8px !important;
	}

	.soundList__item .soundTitle__usernameTitleContainer * {
        color: var(--primary-color);
		font-size: 14pt !important;
        font-stretch: condensed;
		font-weight: normal !important;
	}

	.soundTitle__username span {
        color: var(--secondary-color) !important;
		font-size: 14pt !important;
        font-stretch: condensed;
	}

	.soundTitle .soundTitle__title {
        color: var(--primary-color) !important;
		font-size: 16pt !important;
        font-stretch: condensed !important;
		min-height: 30px !important;
		line-height: 25pt !important;
	}

	.soundList__item .waveform__scene.waveform__layer canvas:nth-child(2),
	.soundList__item .waveform__scene.waveform__layer canvas:nth-child(3),
	.soundList__item .commentPopover,
	.historicalPlays__item canvas:nth-child(2),
	.historicalPlays__item canvas:nth-child(3),
	.historicalPlays__item .commentPopover,
	.searchList__item canvas:nth-child(2),
	.searchList__item canvas:nth-child(3),.searchList__item .commentPopover {

		/*** STREAM SOUND LIST ITEM VISUALIZER ***/
		display: none !important;
	}

	.visualSound__wrapper.g-all-transitions-300 {
		margin-left: 0px !important;
	}

	.soundList__item .commentForm.commentForm__transition.m-small.visible,
	.historicalPlays__item .commentForm.commentForm__transition.m-small.visible,
	.searchList__item .commentForm.commentForm__transition.m-small.visible {

		/*** STREAM SOUND LIST ITEM COMMENT ***/
		display: none !important;
	}

	.soundList__item .sc-button-group .sc-button-small,
	.historicalPlays__item .sc-button-group .sc-button-small,
	.searchList__item .sc-button-group .sc-button-small {
		background: #212121;
		border: none !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-size: 12pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		line-height: 7.5px !important;
		height: 38px !important;
		min-width: 45px !important;
		padding-right: 15px;
		padding-left: 15px;
		text-indent: 0px !important;
		text-transform: uppercase !important;
		margin-right: 6px !important;
	}

	.soundList__item .sc-button-small::before,
	.historicalPlays__item .sc-button-small::before,
	.searchList__item .sc-button-small::before {
		display: none;
	}

    .sound__soundActions {
     	background: transparent !important;   
    }

	.soundList__item .sc-button-like,
	.historicalPlays__item .sc-button-like,
	.searchList__item .sc-button-like {
		background: #2CC990 !important;
	}

	.soundList__item .sc-button-like.sc-button-selected,
	.historicalPlays__item .sc-button-like.sc-button-selected,
	.searchList__item .sc-button-like.sc-button-selected {
		background: #FF6766 !important;
	}

	.soundList__item .sc-button-repost,
	.historicalPlays__item .sc-button-repost,
	.searchList__item .sc-button-repost {
		background: #FF9F55 !important;
	}

	.soundList__item .sc-button-share,
	.historicalPlays__item .sc-button-share,
	.searchList__item .sc-button-share {
		background: #D4D4D4 !important;
	}

	.soundList__item .sc-button-more,
	.soundList__item .sc-button-queue,
	.historicalPlays__item .sc-button-more,
	.historicalPlays__item .sc-button-queue,
	.searchList__item .sc-button-more,
	.searchList__item .sc-button-queue {
		background: #60646D !important;
	}

	.soundList__item .soundActions__purchaseLink,
	.historicalPlays__item .soundActions__purchaseLink,
	.searchList__item .soundActions__purchaseLink,
	.soundList__item .commentForm__wrapper.commentForm__transition,
	.historicalPlays__item .commentForm__wrapper.commentForm__transition,
	.searchList__item .commentForm__wrapper.commentForm__transition {
		display: none !important;
	}

	.sound__footer.noDivider {
		position: absolute !important;
		bottom: -65px !important;
	}

	.soundList__item .compactTrackList,
	.historicalPlays__item .compactTrackList,
	.searchList__item .compactTrackList {

		/*** STREAM SOUND LIST COMPACT TRACK LIST ***/
        border: 1px solid var(--border-color) !important;
		border-radius: 1px !important;
	}

    .soundList__item .compactTrackList .sc-border-light-bottom,
    .historicalPlays__item .compactTrackList .sc-border-light-bottom,
    .searchList__item .compactTrackList .sc-border-light-bottom {
        border-bottom: 1px solid var(--border-color) !important;
    }

    .compactTrackListItem.clickToPlay.active, 
    .compactTrackListItem.clickToPlay:hover, 
    .compactTrackListItem.clickToPlay:focus,
    .compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional {
        background: transparent !important;
        opacity: .8 !important;
    }

	.compactTrackListItem * {
        color: var(--primary-color);
		font-size: 14pt !important;
        font-stretch: condensed;
	}

	.compactTrackList.expanded .compactTrackList__listContainer {
		width: 100% !important;
	}

	.soundList__item .compactTrackListItem__plays,
	.historicalPlays__item .compactTrackListItem__plays,
	.searchList__item .compactTrackListItem__plays {
		display: none !important;
	}

	.compactTrackList__moreLink {
		font-size: 14pt !important;
		font-stretch: condensed;
		font-weight: bold !important;
		text-transform: uppercase !important;
	}

	.soundList__item .soundTitle__additionalContainer,
	.historicalPlays__item .soundTitle__additionalContainer,
	.searchList__item .soundTitle__additionalContainer {

		/*** STREAM SOUND LIST ITEM TAGS ***/
		font-stretch: condensed !important;
		letter-spacing: .1px !important;
		text-transform: uppercase !important;
		position: absolute !important;
